import React, {
  useState,
  useEffect,
  useCallback,
  createContext,
  useContext,
} from "react";
import {
  Search,
  Home,
  Settings,
  RefreshCw,
  ExternalLink,
  Clock,
  Database,
  User,
  LogOut,
  LogIn,
} from "lucide-react";

// API Configuration
const API_BASE_URL = "http://localhost:8000";

// Auth Context
const AuthContext = createContext(null);

// Google OAuth Hook
const useGoogleAuth = () => {
  const [isLoading, setIsLoading] = useState(false);

  const signInWithGoogle = async () => {
    setIsLoading(true);
    try {
      // Load Google Sign-In script
      if (!window.google) {
        await loadGoogleScript();
      }

      // Initialize Google Sign-In
      await new Promise((resolve, reject) => {
        window.google.accounts.id.initialize({
          client_id:
            "1084786503424-52jq2scgdjtimm0f0sg7g3l5l3nts4on.apps.googleusercontent.com", // Replace with your client ID
          callback: async (response) => {
            try {
              // Send token to backend
              const backendResponse = await fetch(
                `${API_BASE_URL}/auth/google`,
                {
                  method: "POST",
                  headers: { "Content-Type": "application/json" },
                  body: JSON.stringify({ access_token: response.credential }),
                }
              );

              if (backendResponse.ok) {
                const data = await backendResponse.json();
                localStorage.setItem("auth_token", data.access_token);
                localStorage.setItem("user_data", JSON.stringify(data.user));
                resolve(data);
              } else {
                reject(new Error("Authentication failed"));
              }
            } catch (error) {
              reject(error);
            }
          },
        });

        // Show the sign-in prompt
        window.google.accounts.id.prompt();
      });

      setIsLoading(false);
      window.location.reload(); // Refresh to update auth state
    } catch (error) {
      console.error("Google sign-in failed:", error);
      setIsLoading(false);
    }
  };

  return { signInWithGoogle, isLoading };
};

// Load Google Script
const loadGoogleScript = () => {
  return new Promise((resolve, reject) => {
    if (document.getElementById("google-signin-script")) {
      resolve();
      return;
    }

    const script = document.createElement("script");
    script.id = "google-signin-script";
    script.src = "https://accounts.google.com/gsi/client";
    script.onload = resolve;
    script.onerror = reject;
    document.head.appendChild(script);
  });
};

// Auth Provider Component
const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check for stored auth
    const storedToken = localStorage.getItem("auth_token");
    const storedUser = localStorage.getItem("user_data");

    if (storedToken && storedUser) {
      setToken(storedToken);
      setUser(JSON.parse(storedUser));
    }
    setLoading(false);
  }, []);

  const logout = () => {
    localStorage.removeItem("auth_token");
    localStorage.removeItem("user_data");
    setToken(null);
    setUser(null);
  };

  const value = {
    user,
    token,
    isAuthenticated: !!user,
    loading,
    logout,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

// Hook to use auth context
const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};

// Enhanced API utility with auth
const apiCall = async (endpoint, options = {}) => {
  const token = localStorage.getItem("auth_token");

  try {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      headers: {
        "Content-Type": "application/json",
        ...(token && { Authorization: `Bearer ${token}` }),
        ...options.headers,
      },
      ...options,
    });

    if (response.status === 401) {
      // Token expired, logout
      localStorage.removeItem("auth_token");
      localStorage.removeItem("user_data");
      window.location.reload();
      return;
    }

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    return await response.json();
  } catch (error) {
    console.error(`API call failed for ${endpoint}:`, error);
    throw error;
  }
};

// Login Component
const LoginComponent = () => {
  const { signInWithGoogle, isLoading } = useGoogleAuth();

  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: "#f9fafb",
        fontFamily:
          '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
      }}
    >
      <div
        style={{
          backgroundColor: "white",
          padding: "48px",
          borderRadius: "12px",
          boxShadow: "0 4px 6px -1px rgba(0, 0, 0, 0.1)",
          textAlign: "center",
          maxWidth: "400px",
          width: "100%",
        }}
      >
        <div
          style={{
            fontSize: "48px",
            marginBottom: "24px",
          }}
        >
          ðŸ”
        </div>

        <h1
          style={{
            fontSize: "24px",
            fontWeight: "700",
            color: "#111827",
            marginBottom: "8px",
          }}
        >
          Universal Search
        </h1>

        <p
          style={{
            color: "#6b7280",
            marginBottom: "32px",
            fontSize: "16px",
          }}
        >
          Search across all your apps and files in one place
        </p>

        <button
          onClick={signInWithGoogle}
          disabled={isLoading}
          style={{
