    const icons = {
      gmail: "âœ‰ï¸",
      drive: "ðŸ“„",
      notion: "ðŸ“",
      files: "ðŸ“",
      bookmarks: "ðŸ”–",
      slack: "ðŸ’¬",
    };
    return icons[source] || "ðŸ“„";
  };

  const getSourceColor = (source) => {
    const colors = {
      gmail: "#EA4335",
      drive: "#4285F4",
      notion: "#000000",
      files: "#6B7280",
      bookmarks: "#F59E0B",
      slack: "#4A154B",
