// Main Search Interface (enhanced with auth)
const SearchInterface = () => {
  const { user, isAuthenticated } = useAuth();
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [searchStats, setSearchStats] = useState(null);
  const [selectedSources, setSelectedSources] = useState([]);
  const [availableSources, setAvailableSources] = useState([]);

  const debouncedQuery = useDebounce(query, 300);

  // Load available sources on mount
  useEffect(() => {
    const loadSources = async () => {
      try {
        const response = await apiCall("/sources");
        setAvailableSources(response.sources);
      } catch (error) {
        console.error("Failed to load sources:", error);
      }
    };
    loadSources();
  }, [isAuthenticated]);

  // Perform search when debounced query changes
  useEffect(() => {
    if (debouncedQuery.trim()) {
      performSearch(debouncedQuery);
    } else {
      setResults([]);
      setSearchStats(null);
    }
  }, [debouncedQuery, selectedSources, isAuthenticated]);

  const performSearch = async (searchQuery) => {
    setIsSearching(true);
    try {
      const response = await apiCall("/search", {
        method: "POST",
        body: JSON.stringify({
          query: searchQuery,
          sources: selectedSources.length > 0 ? selectedSources : null,
          limit: 20,
        }),
      });

      setResults(response.results);
      setSearchStats({
        totalResults: response.total_results,
        searchTimeMs: response.search_time_ms,
        sourcesSearched: response.sources_searched,
      });
    } catch (error) {
      console.error("Search failed:", error);
      setResults([]);
      setSearchStats(null);
    } finally {
      setIsSearching(false);
    }
  };

  const toggleSource = (sourceName) => {
    setSelectedSources((prev) =>
      prev.includes(sourceName)
        ? prev.filter((s) => s !== sourceName)
        : [...prev, sourceName]
    );
  };

  // Show auth warning for protected sources
  const getSourceAvailability = (source) => {
    if (!source.requires_auth) return "available";
    if (!isAuthenticated) return "requires_auth";
    if (!source.connected) return "not_connected";
    return "available";
  };

  return (
    <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
      {/* Search Header */}
      <div
        style={{
          padding: "24px",
          borderBottom: "1px solid #E5E7EB",
          backgroundColor: "white",
        }}
      >
        <div style={{ position: "relative", marginBottom: "16px" }}>
          <Search
            size={20}
            style={{
              position: "absolute",
              left: "16px",
              top: "50%",
              transform: "translateY(-50%)",
              color: "#9CA3AF",
            }}
          />
          <input
            type="text"
            placeholder={
              isAuthenticated
                ? "Search across all your apps and files..."
                : "Search local files and bookmarks (Sign in for Gmail, Drive, etc.)"
            }
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            style={{
              width: "100%",
              paddingLeft: "48px",
              paddingRight: "16px",
              paddingTop: "14px",
              paddingBottom: "14px",
              border: "2px solid #E5E7EB",
              borderRadius: "12px",
              fontSize: "16px",
              outline: "none",
              transition: "all 0.2s",
              backgroundColor: "white",
            }}
            onFocus={(e) => {
              e.target.style.borderColor = "#3B82F6";
              e.target.style.boxShadow = "0 0 0 3px rgba(59, 130, 246, 0.1)";
            }}
            onBlur={(e) => {
              e.target.style.borderColor = "#E5E7EB";
              e.target.style.boxShadow = "none";
            }}
          />
        </div>

        {/* Auth Status & Source Filters */}
        <div
          style={{
            display: "flex",
            gap: "12px",
            flexWrap: "wrap",
            alignItems: "center",
          }}
        >
          <span
            style={{
              fontSize: "14px",
              color: "#6B7280",
              fontWeight: "500",
            }}
          >
            Search in:
          </span>

          {availableSources.map((source) => {
            const availability = getSourceAvailability(source);
            const isDisabled = availability === "requires_auth";

            return (
              <button
                key={source.name}
                onClick={() => !isDisabled && toggleSource(source.name)}
                disabled={isDisabled}
                style={{
                  padding: "6px 12px",
                  border: "1px solid #E5E7EB",
                  borderRadius: "20px",
                  fontSize: "12px",
                  fontWeight: "500",
                  cursor: isDisabled ? "not-allowed" : "pointer",
                  transition: "all 0.2s",
                  backgroundColor: selectedSources.includes(source.name)
                    ? "#3B82F6"
                    : isDisabled
                    ? "#F3F4F6"
                    : "white",
                  color: selectedSources.includes(source.name)
                    ? "white"
                    : isDisabled
                    ? "#9CA3AF"
                    : "#374151",
                  opacity: isDisabled ? 0.6 : 1,
                }}
                title={isDisabled ? "Sign in to access this source" : ""}
              >
                {source.display_name}
                {availability === "requires_auth" && " ðŸ”’"}
                {source.connected && availability === "available" && (
                  <span style={{ marginLeft: "4px", opacity: 0.7 }}>
                    ({source.total_items})
                  </span>
                )}
              </button>
            );
          })}
        </div>

        {/* Auth Prompt */}
        {!isAuthenticated && (
          <div
            style={{
              marginTop: "12px",
              padding: "12px",
              backgroundColor: "#FEF3C7",
              border: "1px solid #F59E0B",
              borderRadius: "8px",
              fontSize: "14px",
              color: "#92400E",
            }}
          >
            ðŸ’¡ <strong>Sign in with Google</strong> to search your Gmail, Drive,
            and other connected apps
          </div>
        )}
      </div>

      {/* Search Results */}
      <div
        style={{
          flex: 1,
          overflow: "auto",
          backgroundColor: "#F9FAFB",
          padding: "24px",
        }}
      >
        <div style={{ maxWidth: "900px", margin: "0 auto" }}>
          {/* Search Stats */}
          {searchStats && (
            <div
              style={{
                marginBottom: "20px",
                fontSize: "14px",
                color: "#6B7280",
                display: "flex",
                alignItems: "center",
                gap: "16px",
              }}
            >
              <span>
                {searchStats.totalResults} results found in{" "}
                {searchStats.searchTimeMs}ms
              </span>
              <span>Searched: {searchStats.sourcesSearched.join(", ")}</span>
              {user && (
                <span style={{ color: "#10B981" }}>
                  âœ“ Authenticated as {user.name}
                </span>
              )}
            </div>
          )}

          {/* Loading State */}
          {isSearching && (
            <div
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                padding: "40px",
                color: "#6B7280",
              }}
            >
              <div
                style={{
                  width: "32px",
                  height: "32px",
                  border: "3px solid #F3F4F6",
                  borderTop: "3px solid #3B82F6",
                  borderRadius: "50%",
                  animation: "spin 1s linear infinite",
                  marginRight: "12px",
                }}
              />
              Searching across all sources...
            </div>
          )}

          {/* Results */}
          {!isSearching && results.length > 0 && (
            <div>
              {results.map((result) => (
                <SearchResultItem key={result.id} result={result} />
              ))}
            </div>
          )}

          {/* No Results */}
          {!isSearching && query && results.length === 0 && (
            <div
              style={{
                textAlign: "center",
                padding: "60px 20px",
                color: "#6B7280",
              }}
            >
              <Search
                size={48}
                style={{ marginBottom: "16px", opacity: 0.5 }}
              />
              <h3 style={{ fontSize: "18px", marginBottom: "8px" }}>
                No results found for "{query}"
              </h3>
              <p style={{ fontSize: "14px" }}>
                Try different keywords or{" "}
                {!isAuthenticated
                  ? "sign in to search more sources"
                  : "check if your sources are connected"}
                .
              </p>
            </div>
          )}

          {/* Empty State */}
          {!query && (
            <div
              style={{
                textAlign: "center",
                padding: "60px 20px",
                color: "#6B7280",
              }}
            >
              <Search
                size={64}
                style={{ marginBottom: "24px", opacity: 0.3 }}
              />
              <h2
                style={{
                  fontSize: "24px",
                  fontWeight: "600",
                  marginBottom: "12px",
                  color: "#111827",
                }}
              >
                Universal Search
              </h2>
              <p style={{ fontSize: "16px", marginBottom: "8px" }}>
                Search across all your connected apps and files in one place.
              </p>
              <p style={{ fontSize: "14px" }}>
                Try searching for: "project", "analytics", "meeting notes"
              </p>
              {!isAuthenticated && (
                <p
                  style={{
                    fontSize: "14px",
                    marginTop: "16px",
                    color: "#F59E0B",
                  }}
                >
                  Sign in to unlock Gmail, Drive, and other app searches
                </p>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

// Main App Component
const App = () => {
  const [currentPage, setCurrentPage] = useState("search");

  // Add CSS animations
  useEffect(() => {
    const style = document.createElement("style");
    style.textContent = `
      @keyframes spin {
        0% { transform: rotate(0deg); }
        100% { transform: rotate(360deg); }
      }
    `;
    document.head.appendChild(style);
    return () => document.head.removeChild(style);
  }, []);

  return (
    <AuthProvider>
      <AppContent currentPage={currentPage} setCurrentPage={setCurrentPage} />
    </AuthProvider>
  );
};

// App Content (separated to use auth context)
const AppContent = ({ currentPage, setCurrentPage }) => {
  const { user, isAuthenticated, loading, logout } = useAuth();

  if (loading) {
    return (
      <div
        style={{
          height: "100vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          backgroundColor: "#F9FAFB",
        }}
      >
        <div
          style={{
            width: "32px",
            height: "32px",
            border: "3px solid #F3F4F6",
            borderTop: "3px solid #3B82F6",
            borderRadius: "50%",
            animation: "spin 1s linear infinite",
          }}
        />
      </div>
    );
  }

  if (!isAuthenticated) {
    return <LoginComponent />;
  }

  const renderPage = () => {
    switch (currentPage) {
      case "search":
        return <SearchInterface />;
      case "sources":
        return (
          <div
            style={{
              padding: "24px",
              textAlign: "center",
              color: "#6B7280",
            }}
          >
            <Database size={48} style={{ marginBottom: "16px" }} />
            <h2>Sources Management</h2>
            <p>Connected sources management coming soon...</p>
          </div>
        );
      case "settings":
        return (
          <div
            style={{
              padding: "24px",
              textAlign: "center",
              color: "#6B7280",
            }}
          >
            <Settings size={48} style={{ marginBottom: "16px" }} />
            <h2>Settings</h2>
            <p>Settings page coming soon...</p>
          </div>
        );
      default:
        return <SearchInterface />;
    }
  };

  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        fontFamily:
          '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
        backgroundColor: "#F9FAFB",
      }}
    >
      {/* Sidebar */}
      <div
        style={{
          width: "240px",
          backgroundColor: "white",
          borderRight: "1px solid #E5E7EB",
          display: "flex",
          flexDirection: "column",
          padding: "24px 0",
        }}
      >
        <div
          style={{
            padding: "0 24px",
            marginBottom: "32px",
          }}
        >
          <h1
            style={{
              fontSize: "20px",
              fontWeight: "700",
              color: "#111827",
              margin: 0,
            }}
          >
            ðŸ” Universal Search
          </h1>
        </div>

        <nav style={{ flex: 1, marginBottom: "24px" }}>
          {[
            { id: "search", icon: Search, label: "Search" },
            { id: "sources", icon: Database, label: "Sources" },
            { id: "settings", icon: Settings, label: "Settings" },
          ].map((item) => {
            const Icon = item.icon;
            const isActive = currentPage === item.id;

            return (
              <button
                key={item.id}
                onClick={() => setCurrentPage(item.id)}
                style={{
                  display: "flex",
                  alignItems: "center",
                  gap: "12px",
                  width: "100%",
                  padding: "12px 24px",
                  border: "none",
                  backgroundColor: isActive ? "#F3F4F6" : "transparent",
                  color: isActive ? "#111827" : "#6B7280",
                  fontSize: "15px",
                  fontWeight: "500",
                  cursor: "pointer",
                  transition: "all 0.2s",
                  textAlign: "left",
                }}
              >
                <Icon size={18} />
                {item.label}
              </button>
            );
          })}
        </nav>

        {/* User Profile */}
        <div style={{ padding: "0 24px" }}>
          <UserProfile user={user} onLogout={logout} />
        </div>
      </div>

      {/* Main Content */}
      <main style={{ flex: 1, display: "flex", flexDirection: "column" }}>
        {renderPage()}
