                style={{
                  display: "flex",
                  alignItems: "center",
                  gap: "12px",
                  width: "100%",
                  padding: "12px 24px",
                  border: "none",
                  backgroundColor: isActive
                    ? darkMode
                      ? "#3F3F46"
                      : "#F3F4F6"
                    : "transparent",
                  color: isActive
                    ? darkMode
                      ? "#E4E4E7"
                      : "#111827"
                    : darkMode
                    ? "#A1A1AA"
                    : "#6B7280",
                  fontSize: "15px",
                  fontWeight: "500",
                  cursor: "pointer",
                  transition: "all 0.2s",
                  textAlign: "left",
                }}
              >
                <Icon size={18} />
                {item.label}
              </button>
            );
          })}
        </nav>

        <div
          style={{
            padding: "0 24px",
            display: "flex",
            flexDirection: "column",
            gap: "12px",
          }}
        >
          <DarkModeToggle />
          <UserProfile user={user} onLogout={logout} />
        </div>
      </div>

      <main style={{ flex: 1, display: "flex", flexDirection: "column" }}>
        {renderPage()}
      </main>
    </div>
  );
};

const RootApp = () => (
  <ThemeProvider>
    <AuthProvider>
      <App />
    </AuthProvider>
  </ThemeProvider>
);

export default RootApp;
