import React, {
  useState,
  useEffect,
  useCallback,
  createContext,
  useContext,
} from "react";
import {
  Search,
  Home,
  Settings,
  RefreshCw,
  ExternalLink,
  Clock,
  Database,
  User,
  LogOut,
  LogIn,
  Moon,
  Sun,
} from "lucide-react";

// ===============================
// CONFIG
// ===============================
const API_BASE_URL = "http://localhost:8000";
const ThemeContext = createContext();
const AuthContext = createContext(null);

// ===============================
// THEME CONTEXT (Dark Mode Toggle)
// ===============================
const ThemeProvider = ({ children }) => {
  const [darkMode, setDarkMode] = useState(
    localStorage.getItem("darkMode") === "true"
  );

  const toggleTheme = () => {
    const newTheme = !darkMode;
    setDarkMode(newTheme);
    localStorage.setItem("darkMode", newTheme);
  };

  useEffect(() => {
    document.body.style.backgroundColor = darkMode ? "#202123" : "#F9FAFB";
    document.body.style.color = darkMode ? "#E4E4E7" : "#111827";
  }, [darkMode]);

  return (
    <ThemeContext.Provider value={{ darkMode, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

const useTheme = () => useContext(ThemeContext);

// ===============================
// AUTH & GOOGLE SIGN-IN
// ===============================
const useGoogleAuth = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const signInWithGoogle = async () => {
    setIsLoading(true);
    setError(null);
    try {
      if (!window.google) await loadGoogleScript();

      await new Promise((resolve, reject) => {
        const existingContainer = document.getElementById("g_id_onload");
        if (existingContainer) existingContainer.remove();

        window.google.accounts.id.initialize({
          client_id:
            "396804387135-sak8ueujt310gs9if8cv6374cakaoh0k.apps.googleusercontent.com", // Replace with real
          auto_select: false,
          callback: async (response) => {
            try {
              const backendResponse = await fetch(
                `${API_BASE_URL}/auth/google`,
                {
                  method: "POST",
                  credentials: "include",
                  headers: { "Content-Type": "application/json" },
                  body: JSON.stringify({ credential: response.credential }),
                }
              );

              if (!backendResponse.ok) throw new Error("Authentication failed");

              const data = await backendResponse.json();
              localStorage.setItem("auth_token", data.access_token);
              localStorage.setItem("user_data", JSON.stringify(data.user));
              resolve(data);
            } catch (error) {
              reject(error);
            }
          },
        });

        const signInContainer = document.createElement("div");
        signInContainer.id = "g_id_onload";
        document.body.appendChild(signInContainer);

        window.google.accounts.id.prompt();
      });

      setIsLoading(false);
      window.location.reload();
    } catch (error) {
      console.error("Google sign-in failed:", error);
      setError(error.message);
      setIsLoading(false);
    }
  };

  return { signInWithGoogle, isLoading, error };
};

const loadGoogleScript = () =>
  new Promise((resolve, reject) => {
    if (document.getElementById("google-signin-script")) return resolve();
    const script = document.createElement("script");
    script.id = "google-signin-script";
    script.src = "https://accounts.google.com/gsi/client";
    script.onload = resolve;
    script.onerror = reject;
    document.head.appendChild(script);
  });

// ===============================
// AUTH PROVIDER
// ===============================
const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const storedToken = localStorage.getItem("auth_token");
    const storedUser = localStorage.getItem("user_data");
    if (storedToken && storedUser) {
      setToken(storedToken);
      setUser(JSON.parse(storedUser));
    }
    setLoading(false);
  }, []);

  const logout = () => {
    localStorage.removeItem("auth_token");
    localStorage.removeItem("user_data");
    setToken(null);
    setUser(null);
  };

  return (
    <AuthContext.Provider
      value={{ user, token, isAuthenticated: !!user, loading, logout }}
    >
      {children}
    </AuthContext.Provider>
  );
};

const useAuth = () => useContext(AuthContext);

// ===============================
// API Helper
// ===============================
const apiCall = async (endpoint, options = {}) => {
  const token = localStorage.getItem("auth_token");
  const response = await fetch(`${API_BASE_URL}${endpoint}`, {
    headers: {
      "Content-Type": "application/json",
      ...(token && { Authorization: `Bearer ${token}` }),
      ...options.headers,
    },
    ...options,
  });

  if (response.status === 401) {
    localStorage.removeItem("auth_token");
    localStorage.removeItem("user_data");
    window.location.reload();
    return;
  }

  if (!response.ok) throw new Error(`HTTP error: ${response.status}`);
  return response.json();
};

