import React, { useState, useEffect, useCallback } from "react";
import {
  Search,
  Home,
  Settings,
  RefreshCw,
  ExternalLink,
  Clock,
  Database,
} from "lucide-react";

// API Configuration
const API_BASE_URL = "http://localhost:8000";

// Utility function for API calls
const apiCall = async (endpoint, options = {}) => {
  try {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      headers: {
        "Content-Type": "application/json",
        ...options.headers,
      },
      ...options,
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    return await response.json();
  } catch (error) {
    console.error(`API call failed for ${endpoint}:`, error);
    throw error;
  }
};

// Debounce hook
const useDebounce = (value, delay) => {
  const [debouncedValue, setDebouncedValue] = useState(value);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);

    return () => {
      clearTimeout(handler);
    };
  }, [value, delay]);

  return debouncedValue;
};

// Search Result Component
const SearchResultItem = ({ result }) => {
  const getSourceIcon = (source) => {
    const icons = {
      gmail: "笨会ｸ十

#EA4335",
      drive: "#4285F4",
      notion: "#000000",
      files: "#6B7280",
      bookmarks: "#F59E0B",
      slack: "#4A154B",
    };
    return colors[source] || "#6B7280";
  };

  const handleClick = () => {
    if (result.source_url) {
      window.open(result.source_url, "_blank");
    }
  };

  const formatTimestamp = (timestamp) => {
    if (!timestamp) return "";
    try {
      return new Date(timestamp).toLocaleDateString();
    } catch {
      return timestamp;
    }
  };

  return (
    <div
      className="search-result-item"
      onClick={handleClick}
      style={{
        display: "flex",
        alignItems: "flex-start",
        gap: "12px",
        padding: "16px",
        borderRadius: "8px",
        cursor: "pointer",
        transition: "all 0.2s",
        border: "1px solid #E5E7EB",
        marginBottom: "12px",
        backgroundColor: "white",
      }}
      onMouseEnter={(e) => {
        e.target.style.backgroundColor = "#F9FAFB";
        e.target.style.transform = "translateY(-2px)";
        e.target.style.boxShadow = "0 4px 12px rgba(0,0,0,0.1)";
      }}
      onMouseLeave={(e) => {
        e.target.style.backgroundColor = "white";
        e.target.style.transform = "translateY(0px)";
        e.target.style.boxShadow = "none";
      }}
    >
      <div
        style={{
          width: "40px",
          height: "40px",
          borderRadius: "8px",
          backgroundColor: getSourceColor(result.source),
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          fontSize: "18px",
          flexShrink: 0,
        }}
      >
        {getSourceIcon(result.source)}
      </div>

      <div style={{ flex: 1, minWidth: 0 }}>
        <div
          style={{
            display: "flex",
            alignItems: "flex-start",
            justifyContent: "space-between",
            gap: "12px",
          }}
        >
          <div style={{ flex: 1 }}>
            <h3
              style={{
                fontSize: "16px",
                fontWeight: "600",
                color: "#111827",
                margin: "0 0 4px 0",
                lineHeight: "1.4",
              }}
            >
              {result.title}
            </h3>

            <p
              style={{
                fontSize: "14px",
                color: "#6B7280",
                margin: "0 0 8px 0",
                lineHeight: "1.5",
              }}
            >
              {result.snippet}
            </p>

            <div
              style={{
                display: "flex",
                alignItems: "center",
                gap: "12px",
                fontSize: "12px",
                color: "#9CA3AF",
              }}
            >
              <span
                style={{
                  textTransform: "capitalize",
                  fontWeight: "500",
                  color: getSourceColor(result.source),
                }}
              >
                {result.source}
              </span>

              {result.timestamp && (
                <span
                  style={{ display: "flex", alignItems: "center", gap: "4px" }}
                >
                  <Clock size={12} />
                  {formatTimestamp(result.timestamp)}
                </span>
              )}

              <span
                style={{
                  backgroundColor: "#F3F4F6",
                  padding: "2px 6px",
                  borderRadius: "4px",
                  fontSize: "11px",
                }}
              >
                {Math.round(result.relevance_score)}% match
              </span>
            </div>
          </div>

          <ExternalLink size={16} color="#9CA3AF" style={{ flexShrink: 0 }} />
        </div>
      </div>
    </div>
  );
};

// Source Status Component
const SourceCard = ({ source, onReindex }) => {
  const [isReindexing, setIsReindexing] = useState(false);

  const handleReindex = async () => {
    setIsReindexing(true);
    try {
      await apiCall(`/sources/${source.name}/reindex`, { method: "POST" });
      // Optionally refresh source status
    } catch (error) {
      console.error("Reindex failed:", error);
    } finally {
      setIsReindexing(false);
    }
  };

  const getSourceIcon = (name) => {
    const icons = {
      gmail: "笨会ｸ十

white",
        border: "1px solid #E5E7EB",
        borderRadius: "12px",
        padding: "20px",
        textAlign: "center",
      }}
    >
      <div
        style={{
          fontSize: "32px",
          marginBottom: "12px",
        }}
      >
        {getSourceIcon(source.name)}
      </div>

      <h3
        style={{
          fontSize: "16px",
          fontWeight: "600",
          color: "#111827",
          margin: "0 0 8px 0",
        }}
      >
        {source.display_name}
      </h3>

      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          gap: "6px",
          marginBottom: "12px",
        }}
      >
        <div
          style={{
            width: "8px",
            height: "8px",
            borderRadius: "50%",
            backgroundColor: source.connected ? "#10B981" : "#EF4444",
          }}
        />
        <span
          style={{
            fontSize: "14px",
            color: source.connected ? "#10B981" : "#EF4444",
            fontWeight: "500",
          }}
        >
          {source.connected ? "Connected" : "Disconnected"}
        </span>
      </div>

      <div
        style={{
          fontSize: "12px",
          color: "#6B7280",
          marginBottom: "12px",
        }}
      >
        {source.total_items} items indexed
      </div>

      <button
        onClick={handleReindex}
        disabled={isReindexing}
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          gap: "6px",
          width: "100%",
          padding: "8px 12px",
          backgroundColor: isReindexing ? "#F3F4F6" : "#F9FAFB",
          border: "1px solid #E5E7EB",
          borderRadius: "6px",
          fontSize: "12px",
          fontWeight: "500",
          color: "#374151",
          cursor: isReindexing ? "not-allowed" : "pointer",
          transition: "all 0.2s",
        }}
      >
        <RefreshCw
          size={12}
          style={{
            animation: isReindexing ? "spin 1s linear infinite" : "none",
          }}
        />
        {isReindexing ? "Reindexing..." : "Reindex"}
      </button>
    </div>
  );
};

// Main Search Interface
const SearchInterface = () => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [searchStats, setSearchStats] = useState(null);
  const [selectedSources, setSelectedSources] = useState([]);
  const [availableSources, setAvailableSources] = useState([]);

  const debouncedQuery = useDebounce(query, 300);

  // Load available sources on mount
  useEffect(() => {
    const loadSources = async () => {
      try {
        const response = await apiCall("/sources");
        setAvailableSources(response.sources);
      } catch (error) {
        console.error("Failed to load sources:", error);
      }
    };
    loadSources();
  }, []);

  // Perform search when debounced query changes
  useEffect(() => {
    if (debouncedQuery.trim()) {
      performSearch(debouncedQuery);
    } else {
      setResults([]);
      setSearchStats(null);
    }
  }, [debouncedQuery, selectedSources]);

  const performSearch = async (searchQuery) => {
    setIsSearching(true);
    try {
      const response = await apiCall("/search", {
        method: "POST",
        body: JSON.stringify({
          query: searchQuery,
          sources: selectedSources.length > 0 ? selectedSources : null,
          limit: 20,
        }),
      });

      setResults(response.results);
      setSearchStats({
        totalResults: response.total_results,
        searchTimeMs: response.search_time_ms,
        sourcesSearched: response.sources_searched,
      });
    } catch (error) {
      console.error("Search failed:", error);
      setResults([]);
      setSearchStats(null);
    } finally {
      setIsSearching(false);
    }
  };

  const toggleSource = (sourceName) => {
    setSelectedSources((prev) =>
      prev.includes(sourceName)
        ? prev.filter((s) => s !== sourceName)
        : [...prev, sourceName]
    );
  };

  return (
    <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
      {/* Search Header */}
      <div
        style={{
          padding: "24px",
          borderBottom: "1px solid #E5E7EB",
          backgroundColor: "white",
        }}
      >
        <div style={{ position: "relative", marginBottom: "16px" }}>
          <Search
            size={20}
            style={{
              position: "absolute",
              left: "16px",
              top: "50%",
              transform: "translateY(-50%)",
              color: "#9CA3AF",
            }}
          />
          <input
            type="text"
            placeholder="Search across all your apps and files..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            style={{
              width: "100%",
              paddingLeft: "48px",
              paddingRight: "16px",
              paddingTop: "14px",
              paddingBottom: "14px",
              border: "2px solid #E5E7EB",
              borderRadius: "12px",
              fontSize: "16px",
              outline: "none",
              transition: "all 0.2s",
              backgroundColor: "white",
            }}
            onFocus={(e) => {
              e.target.style.borderColor = "#3B82F6";
              e.target.style.boxShadow = "0 0 0 3px rgba(59, 130, 246, 0.1)";
            }}
            onBlur={(e) => {
              e.target.style.borderColor = "#E5E7EB";
              e.target.style.boxShadow = "none";
            }}
          />
        </div>

        {/* Source Filters */}
        <div
          style={{
            display: "flex",
            gap: "8px",
            flexWrap: "wrap",
            alignItems: "center",
          }}
        >
          <span
            style={{
              fontSize: "14px",
              color: "#6B7280",
              fontWeight: "500",
            }}
          >
            Search in:
          </span>
          {availableSources.map((source) => (
            <button
              key={source.name}
              onClick={() => toggleSource(source.name)}
              style={{
                padding: "6px 12px",
                border: "1px solid #E5E7EB",
                borderRadius: "20px",
                fontSize: "12px",
                fontWeight: "500",
                cursor: "pointer",
                transition: "all 0.2s",
                backgroundColor: selectedSources.includes(source.name)
                  ? "#3B82F6"
                  : "white",
                color: selectedSources.includes(source.name)
                  ? "white"
                  : "#374151",
              }}
            >
              {source.display_name}
              {source.connected && (
                <span style={{ marginLeft: "4px", opacity: 0.7 }}>
                  ({source.total_items})
                </span>
              )}
            </button>
          ))}
        </div>
      </div>

      {/* Search Results */}
      <div
        style={{
          flex: 1,
          overflow: "auto",
          backgroundColor: "#F9FAFB",
          padding: "24px",
        }}
      >
        <div style={{ maxWidth: "900px", margin: "0 auto" }}>
          {/* Search Stats */}
          {searchStats && (
            <div
              style={{
                marginBottom: "20px",
                fontSize: "14px",
                color: "#6B7280",
                display: "flex",
                alignItems: "center",
                gap: "16px",
              }}
            >
              <span>
                {searchStats.totalResults} results found in{" "}
                {searchStats.searchTimeMs}ms
              </span>
              <span>Searched: {searchStats.sourcesSearched.join(", ")}</span>
            </div>
          )}

          {/* Loading State */}
          {isSearching && (
            <div
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                padding: "40px",
                color: "#6B7280",
              }}
            >
              <div
                style={{
                  width: "32px",
                  height: "32px",
                  border: "3px solid #F3F4F6",
                  borderTop: "3px solid #3B82F6",
                  borderRadius: "50%",
                  animation: "spin 1s linear infinite",
                  marginRight: "12px",
                }}
              />
              Searching across all sources...
            </div>
          )}

          {/* Results */}
          {!isSearching && results.length > 0 && (
            <div>
              {results.map((result) => (
                <SearchResultItem key={result.id} result={result} />
              ))}
            </div>
          )}

          {/* No Results */}
          {!isSearching && query && results.length === 0 && (
            <div
              style={{
                textAlign: "center",
                padding: "60px 20px",
                color: "#6B7280",
              }}
            >
              <Search
                size={48}
                style={{ marginBottom: "16px", opacity: 0.5 }}
              />
              <h3 style={{ fontSize: "18px", marginBottom: "8px" }}>
                No results found for "{query}"
              </h3>
              <p style={{ fontSize: "14px" }}>
                Try different keywords or check if your sources are connected.
              </p>
            </div>
          )}

          {/* Empty State */}
          {!query && (
            <div
              style={{
                textAlign: "center",
                padding: "60px 20px",
                color: "#6B7280",
              }}
            >
              <Search
                size={64}
                style={{ marginBottom: "24px", opacity: 0.3 }}
              />
              <h2
                style={{
                  fontSize: "24px",
                  fontWeight: "600",
                  marginBottom: "12px",
                  color: "#111827",
                }}
              >
                Universal Search
              </h2>
              <p style={{ fontSize: "16px", marginBottom: "8px" }}>
                Search across all your connected apps and files in one place.
              </p>
              <p style={{ fontSize: "14px" }}>
                Try searching for: "project", "analytics", "meeting notes"
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

// Sources Management Page
const SourcesPage = () => {
  const [sources, setSources] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadSources = async () => {
      try {
        const response = await apiCall("/sources");
        setSources(response.sources);
      } catch (error) {
        console.error("Failed to load sources:", error);
      } finally {
        setLoading(false);
      }
    };
    loadSources();
  }, []);

  const handleReindex = (sourceName) => {
    // Trigger reindexing - could add success/error states here
    console.log(`Reindexing ${sourceName}`);
  };

  if (loading) {
    return (
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          height: "400px",
        }}
      >
        <div
          style={{
            width: "32px",
            height: "32px",
            border: "3px solid #F3F4F6",
            borderTop: "3px solid #3B82F6",
            borderRadius: "50%",
            animation: "spin 1s linear infinite",
          }}
        />
      </div>
    );
  }

  return (
    <div style={{ padding: "24px" }}>
      <div style={{ maxWidth: "1200px", margin: "0 auto" }}>
        <div style={{ marginBottom: "32px" }}>
          <h1
            style={{
              fontSize: "28px",
              fontWeight: "700",
              color: "#111827",
              marginBottom: "8px",
            }}
          >
            Connected Sources
          </h1>
          <p style={{ color: "#6B7280", fontSize: "16px" }}>
            Manage your connected apps and data sources
          </p>
        </div>

        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fill, minmax(280px, 1fr))",
            gap: "24px",
          }}
        >
          {sources.map((source) => (
            <SourceCard
              key={source.name}
              source={source}
              onReindex={handleReindex}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

// Main App Component
const App = () => {
  const [currentPage, setCurrentPage] = useState("search");

  // Add CSS animations
  useEffect(() => {
    const style = document.createElement("style");
    style.textContent = `
      @keyframes spin {
        0% { transform: rotate(0deg); }
        100% { transform: rotate(360deg); }
      }
    `;
    document.head.appendChild(style);
    return () => document.head.removeChild(style);
  }, []);

  const renderPage = () => {
    switch (currentPage) {
      case "search":
        return <SearchInterface />;
      case "sources":
        return <SourcesPage />;
      case "settings":
        return (
          <div
            style={{
              padding: "24px",
              textAlign: "center",
              color: "#6B7280",
