          >
            <Settings size={48} style={{ marginBottom: "16px" }} />
            <h2>Settings</h2>
            <p>Settings page coming soon...</p>
          </div>
        );
      default:
        return <SearchInterface />;
    }
  };

  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        fontFamily:
          '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
        backgroundColor: "#F9FAFB",
      }}
    >
      {/* Sidebar */}
      <div
        style={{
          width: "240px",
          backgroundColor: "white",
          borderRight: "1px solid #E5E7EB",
          display: "flex",
          flexDirection: "column",
          padding: "24px 0",
        }}
      >
        <div
          style={{
            padding: "0 24px",
            marginBottom: "32px",
          }}
        >
          <h1
            style={{
              fontSize: "20px",
              fontWeight: "700",
              color: "#111827",
              margin: 0,
            }}
          >
            沐 Universal Search
          </h1>
        </div>

        <nav style={{ flex: 1 }}>
          {[
            { id: "search", icon: Search, label: "Search" },
            { id: "sources", icon: Database, label: "Sources" },
            { id: "settings", icon: Settings, label: "Settings" },
          ].map((item) => {
            const Icon = item.icon;
            const isActive = currentPage === item.id;

            return (
              <button
                key={item.id}
                onClick={() => setCurrentPage(item.id)}
                style={{
                  display: "flex",
                  alignItems: "center",
                  gap: "12px",
                  width: "100%",
                  padding: "12px 24px",
                  border: "none",
                  backgroundColor: isActive ? "#F3F4F6" : "transparent",
                  color: isActive ? "#111827" : "#6B7280",
                  fontSize: "15px",
                  fontWeight: "500",
                  cursor: "pointer",
                  transition: "all 0.2s",
                  textAlign: "left",
                }}
              >
