import React, { useState, useEffect } from "react";
import { Search, Home, Star, Settings } from "lucide-react";

const styles = {
  app: {
    height: "100vh",
    backgroundColor: "#f9fafb",
    display: "flex",
    fontFamily:
      '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
  },
  sidebar: {
    width: "64px",
    backgroundColor: "white",
    borderRight: "1px solid #e5e7eb",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: "16px 0",
    gap: "16px",
  },
  sidebarButton: {
    padding: "8px",
    borderRadius: "8px",
    border: "none",
    cursor: "pointer",
    transition: "all 0.2s",
    backgroundColor: "transparent",
  },
  sidebarButtonActive: {
    backgroundColor: "#dbeafe",
    color: "#2563eb",
  },
  sidebarButtonInactive: {
    color: "#9ca3af",
  },
  main: {
    flex: 1,
    display: "flex",
    flexDirection: "column",
  },
  searchHeader: {
    padding: "24px",
    borderBottom: "1px solid #e5e7eb",
  },
  searchContainer: {
    position: "relative",
  },
  searchInput: {
    width: "100%",
    paddingLeft: "40px",
    paddingRight: "16px",
    paddingTop: "12px",
    paddingBottom: "12px",
    border: "1px solid #d1d5db",
    borderRadius: "8px",
    outline: "none",
    fontSize: "16px",
  },
  searchIcon: {
    position: "absolute",
    left: "12px",
    top: "50%",
    transform: "translateY(-50%)",
    color: "#9ca3af",
  },
  results: {
    flex: 1,
    overflowY: "auto",
    padding: "0 24px",
  },
  resultsContainer: {
    maxWidth: "1024px",
    margin: "0 auto",
    padding: "24px 0",
  },
  resultItem: {
    display: "flex",
    alignItems: "flex-start",
    gap: "12px",
    padding: "12px",
    borderRadius: "8px",
    cursor: "pointer",
    transition: "background-color 0.2s",
    marginBottom: "8px",
  },
  resultItemHover: {
    backgroundColor: "#f9fafb",
  },
  avatar: {
    width: "32px",
    height: "32px",
    borderRadius: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    color: "white",
    fontSize: "14px",
    fontWeight: "500",
    flexShrink: 0,
  },
  resultContent: {
    flex: 1,
    minWidth: 0,
  },
  resultHeader: {
    display: "flex",
    alignItems: "flex-start",
    justifyContent: "space-between",
    gap: "8px",
  },
  resultTitle: {
    fontSize: "14px",
    fontWeight: "500",
    color: "#111827",
    margin: 0,
    overflow: "hidden",
    textOverflow: "ellipsis",
    whiteSpace: "nowrap",
  },
  resultSubtitle: {
    fontSize: "14px",
    color: "#d97706",
    backgroundColor: "#fef3c7",
    padding: "2px 6px",
    borderRadius: "4px",
    display: "inline-block",
    marginTop: "4px",
  },
  resultDescription: {
    fontSize: "12px",
    color: "#6b7280",
    marginTop: "4px",
    margin: 0,
  },
  resultTimestamp: {
    fontSize: "12px",
    color: "#9ca3af",
    flexShrink: 0,
  },
  loading: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    padding: "32px",
  },
  spinner: {
    width: "32px",
    height: "32px",
    border: "2px solid #f3f4f6",
    borderTop: "2px solid #3b82f6",
    borderRadius: "50%",
    animation: "spin 1s linear infinite",
  },
  emptyState: {
    textAlign: "center",
    padding: "32px",
    color: "#6b7280",
  },
  welcomeContainer: {
    flex: 1,
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  welcomeContent: {
    textAlign: "center",
  },
  welcomeTitle: {
    fontSize: "24px",
    fontWeight: "bold",
    color: "#111827",
    marginBottom: "8px",
  },
  welcomeText: {
    color: "#6b7280",
  },
  contentContainer: {
    flex: 1,
    padding: "24px",
  },
  contentTitle: {
    fontSize: "20px",
    fontWeight: "bold",
    color: "#111827",
    marginBottom: "16px",
  },
  sourcesGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
    gap: "16px",
  },
  sourceCard: {
    padding: "16px",
    border: "1px solid #e5e7eb",
    borderRadius: "8px",
    textAlign: "center",
  },
  sourceIcon: {
    width: "48px",
    height: "48px",
    backgroundColor: "#dbeafe",
    borderRadius: "50%",
    margin: "0 auto 8px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    color: "#2563eb",
    fontWeight: "bold",
  },
  sourceTitle: {
    fontSize: "14px",
    fontWeight: "500",
    marginBottom: "4px",
  },
  sourceStatus: {
    fontSize: "12px",
    color: "#10b981",
  },
  settingsContainer: {
    maxWidth: "384px",
  },
  settingsGroup: {
    marginBottom: "16px",
  },
  settingsLabel: {
    display: "block",
    fontSize: "14px",
    fontWeight: "500",
    color: "#374151",
    marginBottom: "4px",
  },
  settingsSelect: {
    width: "100%",
    padding: "8px",
    border: "1px solid #d1d5db",
    borderRadius: "8px",
    fontSize: "14px",
  },
  checkboxContainer: {
    display: "flex",
    alignItems: "center",
    gap: "8px",
  },
  checkboxLabel: {
    fontSize: "14px",
    color: "#374151",
  },
};

// Add CSS animation for spinner
const spinnerCSS = `
@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}
`;

// Mock data to simulate search results
const mockResults = [
  {
    id: 1,
    type: "drive",
    title: "Project Brief - Q4",
    subtitle: "Plannics",
    description: "We discussed the latest in our meeting.",
    timestamp: "6h ago",
    color: "#10b981",
  },
  {
    id: 2,
    type: "gmail",
    title: "Project Brief - Q4",
    subtitle: "Planning",
    description: "Q4 Drive - We discussed the latest in our meeting.",
    timestamp: "1h ago",
    color: "#ef4444",
  },
  {
    id: 3,
    type: "slack",
    title: "Client Meeting",
    subtitle: "Notes",
    description: "",
    timestamp: "",
    color: "#3b82f6",
  },
  {
    id: 4,
    type: "notion",
    title: "Client Meeting Notes",
    subtitle: "Analytics",
    description: "We discussed the Analytics report in our meeting.",
    timestamp: "2h ago",
    color: "#6b7280",
  },
  {
    id: 5,
    type: "slack",
    title: "Project Brief",
    subtitle: "Q4 Planning",
    description: "We discussed the latest in our meeting.",
    timestamp: "7h ago",
    color: "#f59e0b",
  },
  {
    id: 6,
    type: "slack",
    title: "Slack Thread - Website Rederign",
    subtitle: "",
    description: "",
    timestamp: "",
    color: "#9ca3af",
  },
  {
    id: 7,
    type: "slack",
    title: "Slack Thread - Website Redesign",
    subtitle: "Analytics",
    description: "We discussed the latest Analytics in our meeting",
    timestamp: "Oct 25",
    color: "#2563eb",
  },
  {
    id: 8,
    type: "slack",
    title: "Slack Thread - Analytics report in",
    subtitle: "our meeting",
    description: "",
    timestamp: "Oct ago",
    color: "#2563eb",
  },
];

// Component for individual search result items
const SearchResultItem = ({ result }) => {
  const [isHovered, setIsHovered] = useState(false);

  const getAvatarContent = (type) => {
    switch (type) {
      case "drive":
        return "üìä";
      case "gmail":
        return "‚úâÔ∏è";
      case "slack":
        return "üí¨";
      case "notion":
        return "üìù";
      default:
        return "üìÑ";
    }
  };

  return (
    <div
      style={{
        ...styles.resultItem,
        ...(isHovered ? styles.resultItemHover : {}),
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div style={{ ...styles.avatar, backgroundColor: result.color }}>
        {getAvatarContent(result.type)}
      </div>

      <div style={styles.resultContent}>
        <div style={styles.resultHeader}>
          <div style={{ flex: 1 }}>
            <h3 style={styles.resultTitle}>{result.title}</h3>
            {result.subtitle && (
              <span style={styles.resultSubtitle}>{result.subtitle}</span>
            )}
            {result.description && (
              <p style={styles.resultDescription}>{result.description}</p>
            )}
          </div>
          {result.timestamp && (
            <span style={styles.resultTimestamp}>{result.timestamp}</span>
          )}
        </div>
      </div>
    </div>
  );
};

// Sidebar component
const Sidebar = ({ activeSection, setActiveSection }) => {
  const menuItems = [
    { id: "home", icon: Home, label: "Home" },
    { id: "search", icon: Search, label: "Search" },
    { id: "sources", icon: Star, label: "Sources" },
    { id: "settings", icon: Settings, label: "Settings" },
  ];

  return (
    <div style={styles.sidebar}>
      {menuItems.map((item) => {
        const Icon = item.icon;
        const isActive = activeSection === item.id;
        return (
          <button
            key={item.id}
            onClick={() => setActiveSection(item.id)}
            style={{
              ...styles.sidebarButton,
              ...(isActive
                ? styles.sidebarButtonActive
                : styles.sidebarButtonInactive),
            }}
            title={item.label}
          >
            <Icon size={20} />
          </button>
        );
      })}
    </div>
  );
};

// Main search component
const SearchInterface = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [results, setResults] = useState(mockResults);
  const [isSearching, setIsSearching] = useState(false);

  // Simulate search functionality
  const handleSearch = (query) => {
    setSearchQuery(query);
    setIsSearching(true);

    // Simulate API delay
    setTimeout(() => {
      if (query.trim() === "") {
        setResults(mockResults);
      } else {
        const filtered = mockResults.filter(
          (result) =>
            result.title.toLowerCase().includes(query.toLowerCase()) ||
            result.subtitle.toLowerCase().includes(query.toLowerCase()) ||
            result.description.toLowerCase().includes(query.toLowerCase())
        );
        setResults(filtered);
      }
      setIsSearching(false);
    }, 300);
  };

  return (
    <div style={styles.main}>
      {/* Search Header */}
      <div style={styles.searchHeader}>
        <div style={styles.searchContainer}>
          <Search style={styles.searchIcon} size={20} />
          <input
            type="text"
            placeholder="Search across Gmail, Drive, Slack, Notion..."
            value={searchQuery}
            onChange={(e) => handleSearch(e.target.value)}
            style={styles.searchInput}
          />
        </div>
      </div>

      {/* Results */}
      <div style={styles.results}>
        <div style={styles.resultsContainer}>
          {isSearching ? (
            <div style={styles.loading}>
              <div style={styles.spinner}></div>
            </div>
          ) : (
            <>
              {results.length > 0 ? (
                <div>
                  {results.map((result) => (
                    <SearchResultItem key={result.id} result={result} />
                  ))}
                </div>
              ) : (
                <div style={styles.emptyState}>
                  No results found for "{searchQuery}"
                </div>
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
};

// Main App component
const App = () => {
  const [activeSection, setActiveSection] = useState("search");

  useEffect(() => {
    // Add spinner animation CSS
    const style = document.createElement("style");
    style.textContent = spinnerCSS;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);

  const renderContent = () => {
    switch (activeSection) {
      case "search":
        return <SearchInterface />;
      case "home":
        return (
          <div style={styles.welcomeContainer}>
            <div style={styles.welcomeContent}>
              <h2 style={styles.welcomeTitle}>Welcome to Unified Search</h2>
              <p style={styles.welcomeText}>
                Search across all your connected apps and services
              </p>
            </div>
          </div>
        );
      case "sources":
        return (
          <div style={styles.contentContainer}>
            <h2 style={styles.contentTitle}>Connected Sources</h2>
            <div style={styles.sourcesGrid}>
              {["Gmail", "Drive", "Slack", "Notion"].map((source) => (
                <div key={source} style={styles.sourceCard}>
                  <div style={styles.sourceIcon}>{source[0]}</div>
                  <p style={styles.sourceTitle}>{source}</p>
                  <p style={styles.sourceStatus}>Connected</p>
                </div>
              ))}
            </div>
          </div>
        );
      case "settings":
        return (
          <div style={styles.contentContainer}>
            <h2 style={styles.contentTitle}>Settings</h2>
            <div style={styles.settingsContainer}>
              <div style={styles.settingsGroup}>
                <label style={styles.settingsLabel}>Search Preferences</label>
                <select style={styles.settingsSelect}>
                  <option>All Sources</option>
                  <option>Email Only</option>
                  <option>Documents Only</option>
                </select>
              </div>
              <div style={styles.settingsGroup}>
                <label style={styles.checkboxContainer}>
                  <input type="checkbox" defaultChecked />
                  <span style={styles.checkboxLabel}>
                    Enable real-time search
                  </span>
                </label>
              </div>
              <div style={styles.settingsGroup}>
                <label style={styles.checkboxContainer}>
                  <input type="checkbox" />
                  <span style={styles.checkboxLabel}>
                    Show search suggestions
                  </span>
                </label>
              </div>
            </div>
          </div>
        );
      default:
        return <SearchInterface />;
    }
  };

  return (
    <div style={styles.app}>
      <Sidebar
        activeSection={activeSection}
        setActiveSection={setActiveSection}
      />
      <main style={styles.main}>{renderContent()}</main>
    </div>
  );
};

export default App;
