import React, { useState, useEffect } from "react";
import { Search, Home, Star, Settings } from "lucide-react";

// Mock data to simulate search results
const mockResults = [
  {
    id: 1,
    type: "drive",
    title: "Project Brief - Q4",
    subtitle: "Plannics",
    description: "We discussed the latest in our meeting.",
    timestamp: "6h ago",
    icon: "üìä",
    color: "bg-green-500",
  },
  {
    id: 2,
    type: "gmail",
    title: "Project Brief - Q4",
    subtitle: "Planning",
    description: "Q4 Drive - We discussed the latest in our meeting.",
    timestamp: "1h ago",
    icon: "‚úâÔ∏è",
    color: "bg-red-500",
  },
  {
    id: 3,
    type: "slack",
    title: "Client Meeting",
    subtitle: "Notes",
    description: "",
    timestamp: "",
    icon: "üí¨",
    color: "bg-blue-500",
  },
  {
    id: 4,
    type: "notion",
    title: "Client Meeting Notes",
    subtitle: "Analytics",
    description: "We discussed the Analytics report in our meeting.",
    timestamp: "2h ago",
    icon: "üìù",
    color: "bg-gray-600",
  },
  {
    id: 5,
    type: "slack",
    title: "Project Brief",
    subtitle: "Q4 Planning",
    description: "We discussed the latest in our meeting.",
    timestamp: "7h ago",
    icon: "üìã",
    color: "bg-orange-500",
  },
  {
    id: 6,
    type: "slack",
    title: "Slack Thread - Website Rederign",
    subtitle: "",
    description: "",
    timestamp: "",
    icon: "üîó",
    color: "bg-gray-400",
  },
  {
    id: 7,
    type: "slack",
    title: "Slack Thread - Website Redesign",
    subtitle: "Analytics",
    description: "We discussed the latest Analytics in our meeting",
    timestamp: "Oct 25",
    icon: "üìä",
    color: "bg-blue-600",
  },
  {
    id: 8,
    type: "slack",
    title: "Slack Thread - Analytics report in",
    subtitle: "our meeting",
    description: "",
    timestamp: "Oct ago",
    icon: "üìà",
    color: "bg-blue-600",
  },
];

// Component for individual search result items
const SearchResultItem = ({ result }) => {
  const getAvatarContent = (type, color) => {
    const avatarClasses = `w-8 h-8 rounded-full flex items-center justify-center text-white text-sm font-medium ${color}`;

    switch (type) {
      case "drive":
        return <div className={avatarClasses}>üìä</div>;
      case "gmail":
        return <div className={avatarClasses}>‚úâÔ∏è</div>;
      case "slack":
        return <div className={avatarClasses}>üí¨</div>;
      case "notion":
        return <div className={avatarClasses}>üìù</div>;
      default:
        return <div className={avatarClasses}>üìÑ</div>;
    }
  };

  return (
    <div className="flex items-start space-x-3 p-3 hover:bg-gray-50 rounded-lg cursor-pointer transition-colors">
      {getAvatarContent(result.type, result.color)}

      <div className="flex-1 min-w-0">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <h3 className="text-sm font-medium text-gray-900 truncate">
              {result.title}
            </h3>
            {result.subtitle && (
              <p className="text-sm text-yellow-600 bg-yellow-100 inline px-1 rounded mt-1">
                {result.subtitle}
              </p>
            )}
            {result.description && (
              <p className="text-xs text-gray-500 mt-1">{result.description}</p>
            )}
          </div>
          {result.timestamp && (
            <span className="text-xs text-gray-400 ml-2 flex-shrink-0">
              {result.timestamp}
            </span>
          )}
        </div>
      </div>
    </div>
  );
};

// Sidebar component
const Sidebar = ({ activeSection, setActiveSection }) => {
  const menuItems = [
    { id: "home", icon: Home, label: "Home" },
    { id: "search", icon: Search, label: "Search" },
    { id: "sources", icon: Star, label: "Sources" },
    { id: "settings", icon: Settings, label: "Settings" },
  ];

  return (
    <div className="w-16 bg-white border-r border-gray-200 flex flex-col items-center py-4 space-y-4">
      {menuItems.map((item) => {
        const Icon = item.icon;
        return (
          <button
            key={item.id}
            onClick={() => setActiveSection(item.id)}
            className={`p-2 rounded-lg transition-colors ${
              activeSection === item.id
                ? "bg-blue-100 text-blue-600"
                : "text-gray-400 hover:text-gray-600 hover:bg-gray-100"
            }`}
            title={item.label}
          >
            <Icon size={20} />
          </button>
        );
      })}
    </div>
  );
};

// Main search component
const SearchInterface = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [results, setResults] = useState(mockResults);
  const [isSearching, setIsSearching] = useState(false);

  // Simulate search functionality
  const handleSearch = (query) => {
    setSearchQuery(query);
    setIsSearching(true);

    // Simulate API delay
    setTimeout(() => {
      if (query.trim() === "") {
        setResults(mockResults);
      } else {
        const filtered = mockResults.filter(
          (result) =>
            result.title.toLowerCase().includes(query.toLowerCase()) ||
            result.subtitle.toLowerCase().includes(query.toLowerCase()) ||
            result.description.toLowerCase().includes(query.toLowerCase())
        );
        setResults(filtered);
      }
      setIsSearching(false);
    }, 300);
  };

  return (
    <div className="flex-1 flex flex-col">
      {/* Search Header */}
      <div className="p-6 border-b border-gray-200">
        <div className="relative">
          <Search
            className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400"
            size={20}
          />
          <input
            type="text"
            placeholder="Search across Gmail, Drive, Slack, Notion..."
            value={searchQuery}
            onChange={(e) => handleSearch(e.target.value)}
            className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none"
          />
        </div>
      </div>

      {/* Results */}
      <div className="flex-1 overflow-y-auto">
        <div className="max-w-4xl mx-auto p-6">
          {isSearching ? (
            <div className="flex items-center justify-center py-8">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
            </div>
          ) : (
            <>
              {results.length > 0 ? (
                <div className="space-y-2">
                  {results.map((result) => (
                    <SearchResultItem key={result.id} result={result} />
                  ))}
                </div>
              ) : (
                <div className="text-center py-8 text-gray-500">
                  No results found for "{searchQuery}"
                </div>
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
};

